mapper.select:
select count(*)
from category_setting
where 1=1

	and id = #{id}


	and title like concat("%",#{title},"%")


	and banner = #{banner}


	and type = #{type}


	and sorting = #{sorting}


	and status = #{status}


	and create_by = #{createBy}


	and create_date = #{createDate}


	and update_by = #{updateBy}


	and update_date = #{updateDate}

select

from category_setting
where 1=1

	  and id = #{id}


	  and title like concat("%",#{title},"%")


	  and banner = #{banner}


	  and type = #{type}


	  and sorting = #{sorting}


	  and status = #{status}


	  and create_by = #{createBy}


	  and create_date = #{createDate}


	  and update_by = #{updateBy}


	  and update_date = #{updateDate}


	order by ${orderBy}


	limit #{pageNo}, #{pageSize}
