mapper.select:
select count(*)
from groupon_activity_record gar
where 1=1

	and gar.id = #{id}


	and gar.activity_type = #{activityType}


	and gar.activity_id = #{activityId}


	and gar.user_id = #{userId}


	and gar.num = #{num}


	and gar.surplus_num = #{surplusNum}


	and gar.status = #{status}


	and gar.version = #{version}


	and gar.create_date = #{createDate}


	and gar.create_by = #{createBy}


	and gar.update_date = #{updateDate}


	and gar.update_by = #{updateBy}


	and gar.pdk_uid = #{pdkUid}


	 and gar.invitation_user_id = #{invitationUserId}


	 and (select count(id) from groupon_user_record where attend_id = gar.id) > 1


	 and gar.create_date between #{beginTimeStr} and #{endTimeStr}

select gar.id as id, gar.activity_type as activityType, gar.activity_id as activityId, gar.begin_time as beginTime, gar.end_time as endTime, gar.user_id as userId, gar.num as num, gar.surplus_num as surplusNum, gar.status as status, gar.invitation_user_id as invitationUserId, gar.version as version, gar.create_date as createDate, gar.create_by as createBy, gar.update_date as updateDate, gar.update_by as updateBy, gar.pdk_uid as pdkUid, sl.name as userName, sl.image as userLogo, p.distribution_price as distributionPrice, ga.product_id as productId, ga.num as groupNum, ga.price as groupPrice, ga.id as groupId, ga.status as groupStatus, ga.activity_status as activityStatus, (select count(id) from groupon_user_record gur where gur.attend_id=gar.id) as joinNum, p.product_name as productName, p.image as productImage, p.image_main as imageMain, p.image_small as imageSmall, p.status as productStatus, p.product_sketch as productSketch from groupon_activity_record gar left join groupon_activity ga on ga.id = gar.activity_id left join sys_login sl on sl.id = gar.user_id left join product p on p.id = ga.product_id where 1=1
and gar.activity_type in
#{item}
and gar.id not in
#{item}
and gar.id = #{id}
and gar.activity_type = #{activityType}
and gar.activity_id = #{activityId}
and gar.user_id = #{userId}
and gar.num = #{num}
and gar.surplus_num = #{surplusNum}
and gar.status = #{status}
and gar.version = #{version}
and gar.create_date = #{createDate}
and gar.create_by = #{createBy}
and gar.update_date = #{updateDate}
and gar.update_by = #{updateBy}
and gar.pdk_uid = #{pdkUid}
and #{currentTime} >= str_to_date(gar.begin_time,'%Y-%m-%d %H:%i:%s') and #{currentTime} < str_to_date(gar.end_time,'%Y-%m-%d %H:%i:%s')
and #{overTime} >= str_to_date(gar.end_time,'%Y-%m-%d %H:%i:%s')
and #{overTime2} between date_sub(str_to_date(gar.end_time,'%Y-%m-%d %H:%i:%s'), interval #{second} SECOND) and str_to_date(gar.end_time,'%Y-%m-%d %H:%i:%s')
and activity_type not in (#{isNotType})
order by ${orderBy}
limit #{pageNo}, #{pageSize}
