mapper.select:
select t.id as id, t.user_id as userId, (select name from sys_login where id=t.user_id) as userName, (select balance from user_manufacturer where user_id = t.user_id) as settleBalance, t.withdraw_date as withdrawDate, t.withdraw_amount as withdrawAmount, t.status as status, t.create_by as createBy, t.create_date as createDate, t.update_by as updateBy, t.update_date as updateDate, t.version as version from user_manufacturer_withdraw t where 1=1
and t.user_id = #{userId}
and t.status = #{status}
and str_to_date(t.withdraw_date,'%Y-%m-%d') >= #{beginDateStr}
and str_to_date(t.withdraw_date,'%Y-%m-%d') <= #{endDateStr}
order by t.create_date desc,t.user_id
limit #{pageNo} , #{pageSize}

select 
count(*)
from user_manufacturer_withdraw t 
where 1=1

and t.user_id = #{userId} 


and t.status = #{status}


	and str_to_date(t.withdraw_date,'%Y-%m-%d') >= #{beginDateStr}


	and str_to_date(t.withdraw_date,'%Y-%m-%d') <= #{endDateStr}

select t.id as id, t.user_id as userId, t.withdraw_date as withdrawDate, t.withdraw_amount as withdrawAmount, t.status as status, t.create_by as createBy, t.create_date as createDate, t.update_by as updateBy, t.update_date as updateDate, t.remarks as remarks, t.version as version, HOUR(TIMEDIFF(NOW(),t.create_date)) as period from user_manufacturer_withdraw t where t.user_id = #{userId}
and t.status = #{status}
order by t.create_date desc
