mapper.select:
select t.id as id, t.product_id as productId, t.stock as stock, t.sku_color as skuColor, t.sku_format as skuFormat, t.image, t.sorting as sorting, t.status as status, (select name from sys_dict where value=t.status and type = 'status') as statusName, t.remarks as remarks, t.version as version, p.product_name as productName, p.selling_price as sellingPrice, t.stock_num as stockNum, t.type, t.price, t.activity_id as activityId, t.business_code as businessCode, t.create_by as createBy, t.create_date as createDate, t.update_by as updateBy, t.update_date as updateDate, t.sku_color_id as skuColorId, t.sku_format_id as skuFormatId, t.is_delete as isDelete, p.format_value as formatValue, p.color_value as colorValue from product_sku_link t left join product p on t.product_id=p.id where 1=1
and t.product_id=#{productId}
and p.product_name=#{productName}
and t.id=#{id}
and t.status=#{status}
and t.type=#{type}
and t.activity_id = #{activityId}
and t.stock > 0
and t.is_delete = #{isDelete}
order by t.sorting, id desc
limit #{pageNo} , #{pageSize}

	select count(*) from product_sku_link t left join product p on t.product_id=p.id 
	where 1=1 
	
		and t.product_id=#{productId} 
	
	
		and t.sku_color=#{skuColor} 
	
	
		and t.sku_format=#{skuFormat} 
	
	
	    and p.product_name=#{productName} 
    
    
		and t.status=#{status} 
	
	
		and t.type=#{type} 
	
	
		and t.activity_id = #{activityId} 
	
    
		and t.id=#{id} 
	
	
		and t.stock > 0 
	
	
		and t.is_delete = #{isDelete}

select t.id as id,t.product_id as productId,t.stock as stock,t.sku_color as skuColor,t.sku_format as skuFormat,t.sorting as sorting,t.status as status,
(select name from sys_dict where value=t.status and type = 'status') as statusName,t.create_by as createBy,t.create_date as createDate,t.update_by as updateBy,
t.update_date as updateDate,t.remarks as remarks,t.version as version,p.product_name as productName,
t.stock_num as stockNum,t.type,t.price,t.activity_id as activityId,t.business_code as businessCode,t.is_delete as isDelete
from product_sku_link t left join product p on t.product_id=p.id 

where 1=1 

	and t.id=#{id}
	

	and t.product_id=#{productId} 


	and t.type=#{type} 


	and t.is_delete=#{isDelete}

		SELECT DISTINCT (t.sku_color) AS skuColor
        FROM product_sku_link t
        WHERE t.product_id=#{productId} 
        AND t.status=1
        
        AND t.type=#{type}
               
        ORDER BY t.sorting, t.id DESC

		SELECT DISTINCT (t.sku_format) AS skuFormat
        FROM product_sku_link t
        WHERE t.product_id=#{productId} 
        AND t.status=1
        
        AND t.type=#{type}
               
        ORDER BY t.sorting, t.id DESC

mapper.update:
update product_sku_link set status = 1 where id = #{id}
and (sku_color_id is not null and sku_color_id != 0)
and (sku_format_id is not null and sku_format_id != 0)

mapper.select:
select count(*)
from product_sku_link t left join product p on t.product_id=p.id 
where t.id != #{id} 

	and t.sku_color_id = #{skuColorId}


	and t.sku_format_id = #{skuFormatId}

select
	
from product_sku_link
where 1=1
	
	  and Id = #{id}
	
	
	  and product_id = #{productId}
	
	
	  and sku_color = #{skuColor}
	
	
	  and sku_format = #{skuFormat}
	
	
	  and image = #{image}
	
	
	  and stock_num = #{stockNum}
	
	
	  and stock = #{stock}
	
	
	  and sorting = #{sorting}
	
	
	  and status = #{status}
	
	
	  and type = #{type}
	
	
	  and price = #{price}
	
	
	  and activity_id = #{activityId}
	
	
	  and business_code = #{businessCode}
	
	
	  and create_by = #{createBy}
	
	
	  and create_date = #{createDate}
	
	
	  and update_by = #{updateBy}
	
	
	  and update_date = #{updateDate}
	
	
	  and remarks = #{remarks}
	
	
	  and version = #{version}
	
	
	  and is_delete = #{isDelete}
	
	
		and id not in 
		
			#{cids}
		
	

	order by ${orderBy}


	limit #{pageNo}, #{pageSize}

select ifnull(sum(stock),0) from product_sku_link where 1=1
and product_id = #{productId}
and status = #{status}
